/// <reference types="node" />
export default class Pip {
    gitPath: string;
    private logger;
    constructor(gitPath?: string, logger?: Console);
    install(packageName: string, options?: Partial<PipInstallOptions>): Promise<any>;
    run(args: string | string[], options?: Partial<PipRunOptions>): Promise<any>;
}
export interface PipRunOptions {
    cwd?: string;
    dryrun?: boolean;
    pipe?: boolean;
}
export interface PipInstallOptions extends PipRunOptions {
    user?: boolean;
}
